import React from 'react';

class Pokedex extends React.Component {

  constructor() {
    super();
    this.state = {
      pokeInfo: this.props.pokemons[0],
    }
  }
  render() {
    return (
      <div className="pokedex">
        {this.state.pokeInfo}
      </div>
    );
  }
}

export default Pokedex;
